contracts/Wagemore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title Wagemore
 * @dev Prediction Market with FREE UMA Optimistic Oracle V3
 * - Decentralized resolution with community verification
 * - Market creators propose outcomes with refundable bonds
 * - Zero oracle fees (only refundable bonds)
 * - Challenge period for dispute resolution
 */

// UMA Optimistic Oracle V3 Interface
interface OptimisticOracleV3Interface {
    function assertTruth(
        bytes memory claim,
        address asserter,
        address callbackRecipient,
        address escalationManager,
        uint64 liveness,
        address currency,
        uint256 bond,
        bytes32 identifier,
        bytes32 domainId
    ) external payable returns (bytes32 assertionId);

    function settleAssertion(bytes32 assertionId) external;

    function getAssertion(
        bytes32 assertionId
    )
        external
        view
        returns (
            bool,
            address,
            address,
            address,
            uint64,
            address,
            uint256,
            bytes32,
            bytes32,
            bytes memory
        );
}

contract Wagemore is Ownable, ReentrancyGuard, Pausable {
    // =====================================
    // CONFIGURATION
    // =====================================

    enum MarketType {
        Binary,
        Multiple
    }

    // UMA Oracle Configuration
    OptimisticOracleV3Interface public oo;
    uint64 public constant ASSERTION_LIVENESS = 2 hours; // Challenge period
    uint256 public assertionBond = 0.01 ether; // Refundable bond
    bytes32 public constant DEFAULT_IDENTIFIER = bytes32("ASSERT_TRUTH");

    // =====================================
    // EVENTS
    // =====================================

    event MarketCreated(
        uint64 indexed marketId,
        address indexed creator,
        string title,
        string imageUrl,
        string[] options,
        uint8 optionCount,
        MarketType marketType,
        uint64 endTime,
        uint64 timestamp
    );

    event SharesPurchased(
        uint64 indexed marketId,
        address indexed buyer,
        uint8 optionIndex,
        uint64 amount,
        uint64 timestamp
    );

    event ResolutionProposed(
        uint64 indexed marketId,
        address indexed proposer,
        uint8 proposedWinner,
        bytes32 assertionId,
        string evidence,
        uint64 timestamp
    );

    event MarketResolved(
        uint64 indexed marketId,
        uint8 winningOption,
        bytes32 assertionId,
        bool viaOracle,
        uint64 timestamp
    );

    event WinningsClaimed(
        uint64 indexed marketId,
        address indexed claimer,
        uint64 amount,
        uint64 timestamp
    );

    event PlatformFeesWithdrawn(address owner, uint64 amount);
    event CreationFeeUpdated(uint64 newFee);
    event PlatformFeeUpdated(uint64 newBps);
    event AssertionBondUpdated(uint256 newBond);
    event OracleUpdated(address newOracle);

    // =====================================
    // STATE STORAGE
    // =====================================

    struct Market {
        uint64 id;
        address creator;
        uint64 endTime;
        uint64 totalPool;
        uint64[] totalSharesPerOption;
        bool resolved;
        uint8 winningOption;
        uint8 optionCount;
        MarketType marketType;
        bytes32 assertionId; // UMA assertion ID
        bool oracleResolved;
    }

    uint64 public nextMarketId = 1;
    uint64 public platformFeeBps = 1000; // 10.00%
    uint64 public creationFee = 0.001 ether;
    uint64 public accumulatedFees;

    mapping(uint64 => Market) public markets;
    mapping(uint64 => mapping(uint8 => mapping(address => uint256)))
        public bets;
    mapping(bytes32 => uint64) public assertionIdToMarketId;

    // =====================================
    // CONSTRUCTOR
    // =====================================

    /**
     * @param _optimisticOracle Address of UMA Optimistic Oracle V3
     * Pass address(0) to disable oracle (testing only)
     */
    constructor(address _optimisticOracle) Ownable(msg.sender) {
        if (_optimisticOracle != address(0)) {
            oo = OptimisticOracleV3Interface(_optimisticOracle);
        }
    }

    // =====================================
    // MAIN FUNCTIONS
    // =====================================

    function createMarket(
        string calldata title,
        string calldata imageUrl,
        string[] calldata optionLabels,
        uint64 endTime,
        MarketType _marketType
    ) external payable whenNotPaused returns (uint64) {
        require(endTime > block.timestamp, "End time must be in future");
        require(bytes(title).length > 0, "Title required");

        if (_marketType == MarketType.Binary) {
            require(optionLabels.length == 2, "Binary must have 2 options");
        } else {
            require(optionLabels.length >= 2, "Multiple needs 2+ options");
            require(optionLabels.length <= 20, "Max 20 options");
        }

        if (msg.sender != owner()) {
            require(msg.value == creationFee, "Incorrect creation fee");
            accumulatedFees += uint64(msg.value);
        }

        uint64 marketId = nextMarketId++;
        uint8 count = uint8(optionLabels.length);

        markets[marketId] = Market({
            id: marketId,
            creator: msg.sender,
            endTime: endTime,
            totalPool: 0,
            totalSharesPerOption: new uint64[](count),
            resolved: false,
            winningOption: 0,
            optionCount: count,
            marketType: _marketType,
            assertionId: bytes32(0),
            oracleResolved: false
        });

        emit MarketCreated(
            marketId,
            msg.sender,
            title,
            imageUrl,
            optionLabels,
            count,
            _marketType,
            endTime,
            uint64(block.timestamp)
        );

        return marketId;
    }

    function purchaseShares(
        uint64 marketId,
        uint8 optionIndex
    ) external payable whenNotPaused nonReentrant {
        require(msg.value > 0, "Bet amount must be > 0");
        Market storage market = markets[marketId];

        require(market.id != 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(block.timestamp < market.endTime, "Betting phase ended");
        require(optionIndex < market.optionCount, "Invalid option index");

        market.totalPool += uint64(msg.value);
        market.totalSharesPerOption[optionIndex] += uint64(msg.value);
        bets[marketId][optionIndex][msg.sender] += msg.value;

        emit SharesPurchased(
            marketId,
            msg.sender,
            optionIndex,
            uint64(msg.value),
            uint64(block.timestamp)
        );
    }

    /**
     * @dev Propose a resolution using UMA Optimistic Oracle
     * @param marketId The market to resolve
     * @param winningOption The proposed winning option
     * @param evidence URL or IPFS hash with proof
     *
     * REQUIRES: assertionBond (default 0.01 ETH) - REFUNDED if not challenged
     */
    function proposeResolution(
        uint64 marketId,
        uint8 winningOption,
        string calldata evidence
    ) external payable nonReentrant {
        Market storage market = markets[marketId];
        require(market.id != 0, "Invalid market");
        require(!market.resolved, "Already resolved");
        require(block.timestamp >= market.endTime, "Market still active");
        require(winningOption < market.optionCount, "Invalid option");
        require(
            market.assertionId == bytes32(0),
            "Resolution already proposed"
        );

        // Only creator or owner can propose
        require(
            msg.sender == market.creator || msg.sender == owner(),
            "Only creator or owner"
        );

        // Must provide bond
        require(msg.value >= assertionBond, "Insufficient bond");

        // If oracle is disabled, resolve immediately
        if (address(oo) == address(0)) {
            market.resolved = true;
            market.winningOption = winningOption;
            market.oracleResolved = false;

            // Refund bond
            if (msg.value > 0) {
                (bool success, ) = payable(msg.sender).call{value: msg.value}(
                    ""
                );
                require(success, "Refund failed");
            }

            emit MarketResolved(
                marketId,
                winningOption,
                bytes32(0),
                false,
                uint64(block.timestamp)
            );
            return;
        }

        // Construct the claim
        bytes memory claim = abi.encode(marketId, winningOption, evidence);

        // Submit assertion to UMA
        bytes32 assertionId = oo.assertTruth{value: assertionBond}(
            claim,
            msg.sender,
            address(this),
            address(0),
            ASSERTION_LIVENESS,
            address(0),
            assertionBond,
            DEFAULT_IDENTIFIER,
            bytes32(0)
        );

        market.assertionId = assertionId;
        assertionIdToMarketId[assertionId] = marketId;

        // Refund excess bond
        if (msg.value > assertionBond) {
            (bool success, ) = payable(msg.sender).call{
                value: msg.value - assertionBond
            }("");
            require(success, "Refund failed");
        }

        emit ResolutionProposed(
            marketId,
            msg.sender,
            winningOption,
            assertionId,
            evidence,
            uint64(block.timestamp)
        );
    }

    /**
     * @dev Finalize resolution after challenge period
     * Anyone can call this after ASSERTION_LIVENESS passes
     */
    function finalizeResolution(uint64 marketId) external nonReentrant {
        Market storage market = markets[marketId];
        require(market.id != 0, "Invalid market");
        require(!market.resolved, "Already resolved");
        require(market.assertionId != bytes32(0), "No resolution proposed");
        require(address(oo) != address(0), "Oracle not configured");

        // Settle the assertion
        oo.settleAssertion(market.assertionId);

        // Get the assertion result
        (bool resolved, , , , , , , , , bytes memory claim) = oo.getAssertion(
            market.assertionId
        );
        require(resolved, "Assertion not settled");

        // Extract winning option from claim
        (uint64 claimMarketId, uint8 winningOption, ) = abi.decode(
            claim,
            (uint64, uint8, string)
        );
        require(claimMarketId == marketId, "Market ID mismatch");

        market.resolved = true;
        market.oracleResolved = true;
        market.winningOption = winningOption;

        emit MarketResolved(
            marketId,
            winningOption,
            market.assertionId,
            true,
            uint64(block.timestamp)
        );
    }

    /**
     * @dev Emergency override (for testing or broken oracle)
     * Only owner can call, requires waiting period
     */
    function emergencyResolve(
        uint64 marketId,
        uint8 winningOption
    ) external onlyOwner nonReentrant {
        Market storage market = markets[marketId];
        require(market.id != 0, "Invalid market");
        require(!market.resolved, "Already resolved");

        // If oracle is enabled, require waiting period
        if (address(oo) != address(0)) {
            require(
                block.timestamp >= market.endTime + 7 days,
                "Must wait 7 days"
            );
        }

        market.resolved = true;
        market.winningOption = winningOption;
        market.oracleResolved = false;

        emit MarketResolved(
            marketId,
            winningOption,
            bytes32(0),
            false,
            uint64(block.timestamp)
        );
    }

    function claimWinnings(uint64 marketId) external nonReentrant {
        Market storage market = markets[marketId];
        require(market.resolved, "Market not resolved yet");

        uint8 winner = market.winningOption;
        uint256 userStake = bets[marketId][winner][msg.sender];

        require(userStake > 0, "No winning bets to claim");

        bets[marketId][winner][msg.sender] = 0;

        uint64 totalWinnerStakes = market.totalSharesPerOption[winner];
        uint64 marketPool = market.totalPool;

        require(totalWinnerStakes > 0, "Critical logic error");

        uint64 totalFee = (marketPool * platformFeeBps) / 10000;
        uint64 distributablePool = marketPool - totalFee;

        uint256 payout = (userStake * distributablePool) / totalWinnerStakes;
        uint256 feeShare = (userStake * totalFee) / totalWinnerStakes;
        accumulatedFees += uint64(feeShare);

        (bool success, ) = payable(msg.sender).call{value: payout}("");
        require(success, "ETH transfer failed");

        emit WinningsClaimed(
            marketId,
            msg.sender,
            uint64(payout),
            uint64(block.timestamp)
        );
    }

    // =====================================
    // ADMIN FUNCTIONS
    // =====================================

    function withdrawFees() external onlyOwner nonReentrant {
        uint256 amount = accumulatedFees;
        require(amount > 0, "No fees to withdraw");

        accumulatedFees = 0;

        (bool success, ) = payable(owner()).call{value: amount}("");
        require(success, "Transfer failed");

        emit PlatformFeesWithdrawn(owner(), uint64(amount));
    }

    function setCreationFee(uint64 newFee) external onlyOwner {
        creationFee = newFee;
        emit CreationFeeUpdated(newFee);
    }

    function setPlatformFee(uint64 newBps) external onlyOwner {
        require(newBps <= 2000, "Fee cannot exceed 20%");
        platformFeeBps = newBps;
        emit PlatformFeeUpdated(newBps);
    }

    function setAssertionBond(uint256 newBond) external onlyOwner {
        assertionBond = newBond;
        emit AssertionBondUpdated(newBond);
    }

    function setOracle(address newOracle) external onlyOwner {
        oo = OptimisticOracleV3Interface(newOracle);
        emit OracleUpdated(newOracle);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // =====================================
    // VIEW FUNCTIONS
    // =====================================

    function getMarketState(
        uint64 marketId
    )
        external
        view
        returns (
            address creator,
            uint64 endTime,
            uint64 totalPool,
            bool resolved,
            uint8 winningOption,
            uint8 optionCount,
            MarketType marketType,
            bytes32 assertionId,
            bool oracleResolved
        )
    {
        Market storage m = markets[marketId];
        return (
            m.creator,
            m.endTime,
            m.totalPool,
            m.resolved,
            m.winningOption,
            m.optionCount,
            m.marketType,
            m.assertionId,
            m.oracleResolved
        );
    }

    function getUserBet(
        uint64 marketId,
        uint8 optionIndex,
        address user
    ) external view returns (uint256) {
        return bets[marketId][optionIndex][user];
    }

    function isOracleEnabled() external view returns (bool) {
        return address(oo) != address(0);
    }

    // Allow contract to receive ETH
    receive() external payable {}
}

